import * as React from "react"
import { Frame } from "framer"

export function Header(props) {
    return (
        <Frame
            background="#05f"
            color={"#fff"}
            borderRadius={9999}
            width={"60%"}
            height={"100%"}
            center
            initial={{ borderRadius: 9999, width: "60%" }}
            animate={
                props.stucked && {
                    borderRadius: 0,
                    width: "100%",
                    background: "#09f",
                }
            }
            transition={{ duration: 0.1 }}
            style={{ fontSize: 16, fontWeight: "bold" }}
        >
            {props.stucked ? "Stucked" : "Unpinned"}
        </Frame>
    )
}
