import * as React from "react"
import { Frame } from "framer"

export function Header(props) {
    return (
        <Frame
            background="salmon"
            size={"100%"}
            initial={props.stucked && { y: -10, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ ease: "linear" }}
        >
            {props.stucked ? "stucked" : "unpinned"}
        </Frame>
    )
}
